#Region CallThisProcess
# A snippet of code provided as an example how to call this process should the developer be working on a system without access to an editor with auto-complete.
If( 1 = 0 );
    ExecuteProcess( '}bedrock.cube.clone', 'pLogOutput', pLogOutput,
      'pStrictErrorHandling', pStrictErrorHandling,
    	'pSrcCube' ,'', 'pTgtCube', '',
    	'pIncludeRules', 1, 'pIncludeData', 0,
    	'pFilter', '',
    	'pDimDelim', '&', 'pEleStartDelim', '¦', 'pEleDelim', '+',
    	'pSuppressRules', 1, 'pTemp', 1, 'pCubeLogging', 0
    );
EndIf;
#EndRegion CallThisProcess

#****Begin: Generated Statements***
#****End: Generated Statements****

################################################################################################# 
##~~Join the bedrock TM1 community on GitHub https://github.com/cubewise-code/bedrock-5 Ver 5.0~~##
################################################################################################# 

#Region @DOC
# Description:
# This process replicates an existing cube. It can include data & rules too.

# Use case: Intended for development/prototyping.
# 1. Take a snapshot of cube data copying all rules to values.
# 2. Take an exact copy of a cube in a "one click action" as a starting point for prototyping rule changes or developing new features.

# Note:
# * There are parameter options to include data (pIncludeData) and rules (pIncludeRules) with the creation of the cube.
# * If the source cube (pSrcCube) is left blank or doesn't exist in the model, process will terminate withoud doing anything.
# * If the target cube (pTgtCube) already exists in the model, process will terminate withoud doing anything.
# * If the target cube is left blank or is the same as the source cube the cloned cube will inherit the source cube name with "_Clone" appended.
# * If the source cube data only needs to be partially copied, then the pFilter parameter should be entered otherwise all other parameters can be left as is.
# * In productive systems this process may be called internally by other processes (}bedrock.cube.data.copy, }bedrock.cube.data.copy.intercube) if copying data via intermediate cloned cube.
#EndRegion @DOC

### Global Variables
StringGlobalVariable('sProcessReturnCode');
NumericGlobalVariable('nProcessReturnCode');
nProcessReturnCode= 0;

### Constants ###
cThisProcName   = GetProcessName();
cUserName       = TM1User();
cTimeStamp      = TimSt( Now, '\Y\m\d\h\i\s' );
cRandomInt      = NumberToString( INT( RAND( ) * 1000 ));
cTempSub        = cThisProcName |'_'| cTimeStamp |'_'| cRandomInt;
cMsgErrorLevel  = 'ERROR';
cMsgErrorContent= 'User:%cUserName% Process:%cThisProcName% ErrorMsg:%sMessage%';
cLogInfo        = 'Process:%cThisProcName% run with parameters pSrcCube:%pSrcCube%, pTgtCube:%pTgtCube%, pIncludeRules:%pIncludeRules%, pIncludeData:%pIncludeData%, pFilter:%pFilter%, pDimDelim:%pDimDelim%, pEleStartDelim:%pEleStartDelim%, pEleDelim:%pEleDelim%, pSuppressRules:%pSuppressRules%, pTemp:%pTemp%, pCubeLogging:%pCubeLogging%.' ;   
cDimCountMax    = 30 ;

#################################################################################################
#Region - Process Parameters

pJson = IF( JsonType( pJson ) @<> 'object', '{}', pJson );
sMessages = '{"Process": "'|GetProcessName()|'", "User": "'|TM1User()|'", "Info": ["Process: '''|GetProcessName()|''' run by User: '''|TM1User()|'''."], "Error": []}';
pDefaultParameters = '{
  "pDimDelim":            "&",
  "pEleDelim":            "+",
  "pEleStartDelim":       "¦",
  "pFilter":              "",
  "pSrcCube":             null,
  "pTgtCube":             "",
  "pIncludeData":         0,
  "pIncludeRules":        1,
  "pLogOutput":           0,
  "pStrictErrorHandling": 0,
  "pSuppressRules":       1,
  "pTemp":                1
}';

pPassedParameters = '{
  "pDimDelim":            '|StringToJson  ( pDimDelim            )|',
  "pEleDelim":            '|StringToJson  ( pEleDelim            )|',
  "pEleStartDelim":       '|StringToJson  ( pEleStartDelim       )|',
  "pFilter":              '|StringToJson  ( pFilter              )|',
  "pSrcCube":             '|StringToJson  ( pSrcCube             )|',
  "pTgtCube":             '|StringToJson  ( pTgtCube             )|',
  "pIncludeData":         '|NumberToString( pIncludeData         )|',
  "pIncludeRules":        '|NumberToString( pIncludeRules        )|',
  "pLogOutput":           '|NumberToString( pLogOutput           )|',
  "pStrictErrorHandling": '|NumberToString( pStrictErrorHandling )|',
  "pSuppressRules":       '|NumberToString( pSuppressRules       )|',
  "pTemp":                '|NumberToString( pTemp                )|'
}';

pJson = JsonMergePatch( pPassedParameters, pJson );
# String Parameters
pDimDelim            =                 JsonToString( JsonGet( pJson, 'pDimDelim'          ) );
pEleDelim            =                 JsonToString( JsonGet( pJson, 'pEleDelim'          ) );
pEleStartDelim       =                 JsonToString( JsonGet( pJson, 'pEleStartDelim'     ) );
pFilter              =                 JsonToString( JsonGet( pJson, 'pFilter'            ) );
pSrcCube             =                 JsonToString( JsonGet( pJson, 'pSrcCube'           ) );
pTgtCube             =                 JsonToString( JsonGet( pJson, 'pTgtCube'           ) );
# Numeric Parameters
pIncludeData         = StringToNumber( JsonToString( JsonGet( pJson, 'pIncludeData'       ) ) );
pIncludeRules        = StringToNumber( JsonToString( JsonGet( pJson, 'pIncludeRules'      ) ) );
pLogOutput           = StringToNumber( JsonToString( JsonGet( pJson, 'pLogOutput'         ) ) );
pStrictErrorHandling = StringToNumber( JsonToString( JsonGet( pJson, 'pStrictErrorHandling' ) ) );
pSuppressRules       = StringToNumber( JsonToString( JsonGet( pJson, 'pSuppressRules'     ) ) );
pTemp                = StringToNumber( JsonToString( JsonGet( pJson, 'pTemp'              ) ) );

### Get the parameters that differ from the default
pDiffer = JsonDiff( pDefaultParameters, pJson );
nSize = JsonSize( pDiffer );
WHILE( nSize > 0 );
  nSize = nSize - 1;
  pDiffer = JsonReplace( pDiffer, '/'|NumberToString( nSize )|'/op', '"add"' );
END;
pJson = JsonPatch( '{}', pDiffer );

### LogOutput parameters
sMessages = JsonAdd( sMessages, '/Info/-', pJson );
IF( pLogOutput = 1 );
  LogOutput('INFO', JsonToString( sMessages, '/Info/0', 0, ' ' ) );
ENDIF;

#EndRegion - Process Parameters
#################################################################################################

## LogOutput parameters
IF( pLogoutput = 1 );
  LogOutput('INFO', Expand( cLogInfo ) );   
ENDIF;

### Initialise ###
nErrors         = 0;
nDataCheck      = 0;
sDimCountMax    = NumberToString( cDimCountMax );
sDimsString     = '';
sDelim          = '+';

### Validate Parameters ###

## Default filter delimiters
If( pDimDelim     @= '' );
    pDimDelim     = '&';
EndIf;
If( pEleStartDelim@= '' );
    pEleStartDelim= '¦';
EndIf;
If( pEleDelim     @= '' );
    pEleDelim     = '+';
EndIf;

# Validate source cube
If( Trim( pSrcCube ) @= '' );
    nErrors = 1;
    sMessage = 'No cube specified.';
    LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
ElseIf( CubeExists( pSrcCube ) = 0 );    
    sMessage = Expand( 'Invalid source cube specified: %pSrcCube%.' );
    nErrors = 1;
    LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
EndIf;

# Validate target cube
If( pTgtCube @= '' % pTgtCube @= pSrcCube );
    pTgtCube = pSrcCube | '_Clone';
EndIf;
If( CubeExists( pTgtCube ) = 1 );    
    sMessage = Expand( 'Invalid target cube : %pTgtCube%.' );
    nErrors = 1;
    LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
EndIf;

### Create the clone cube ###
nDimCount = 1;
While( TabDim( pSrcCube, nDimCount ) @<> '' );
  sDimName = TabDim (pSrcCube, nDimCount);
  sDimsString = sDimsString | sDimName | sDelim;
  nDimCount = nDimCount + 1;
End;
nDimCount = nDimCount - 1;
sDimsString = Subst(sDimsString,1,long(sDimsString)-long(sDelim));

If( nDimCount > cDimCountMax );
  nErrors = 1;
  sMessage = Expand( 'Cube has too many dimensions: %pSrcCube% max %sDimCountMax% dims catered for, TI must be altered to accomodate.' );
  DataSourceType = 'NULL';
  LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
EndIf;

### Check for errors before continuing
If( nErrors <> 0 );
  If( pStrictErrorHandling = 1 ); 
      ProcessQuit; 
  Else;
      ProcessBreak;
  EndIf;
EndIf;

sProc = '}bedrock.cube.create';
nRet = ExecuteProcess( sProc,
  'pLogOutput', pLogOutput,
  'pStrictErrorHandling', pStrictErrorHandling,
  'pCube', pTgtCube,
  'pDims', sDimsString,
  'pRecreate', 1,
  'pDelim', sDelim
  );

IF(nRet <> 0);
  sMessage = 'Error creating the target cube.';
  nErrors = 1;
  LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
  If( pStrictErrorHandling = 1 ); 
      ProcessQuit; 
  Else;
      ProcessBreak;
  EndIf;
ENDIF;

### copy data ####
If( pIncludeData = 1 );
nRet = ExecuteProcess('}bedrock.cube.data.copy.intercube',
    'pLogOutput', pLogOutput,
    'pStrictErrorHandling', pStrictErrorHandling,
  	'pSrcCube',pSrcCube,
  	'pFilter',pFilter,
  	'pTgtCube',pTgtCube,
  	'pMappingToNewDims','',
  	'pSuppressConsol',1,
  	'pSuppressRules',pSuppressRules,
  	'pZeroTarget',0,
  	'pZeroSource',0,
  	'pFactor',1,
    'pDimDelim', pDimDelim,
    'pEleStartDelim', pEleStartDelim,
    'pEleDelim', pEleDelim,
    'pTemp', pTemp,
    'pCubeLogging', pCubeLogging);

  IF(nRet <> 0);
    sMessage = 'Error copying data.';
    nErrors = nErrors + 1;
    LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
    If( pStrictErrorHandling = 1 ); 
        ProcessQuit; 
    Else;
        ProcessBreak;
    EndIf;
  ENDIF;

EndIf;

### End Prolog ###

EpilogProcedure:
#****Begin: Generated Statements***
#****End: Generated Statements****

################################################################################################# 
##~~Join the bedrock TM1 community on GitHub https://github.com/cubewise-code/bedrock-5 Ver 5.0~~##
################################################################################################# 

### Attach rules to cloned cube ###
If( nErrors = 0 & pIncludeRules = 1 );
  sRule=CubeRuleGet( pSrcCube );
  If( sRule@<> '' );
    If( nErrors = 0 );
      CubeRuleSet(pTgtCube, sRule);
    EndIf;
  EndIf;
EndIf;
    
### Return code & final error message handling
If( nErrors > 0 );
    sMessage = 'the process incurred at least 1 error. Please see above lines in this file for more details.';
    nProcessReturnCode = 0;
    LogOutput( cMsgErrorLevel, Expand( cMsgErrorContent ) );
    sProcessReturnCode = Expand( '%sProcessReturnCode% Process:%cThisProcName% completed with errors. Check tm1server.log for details.' );
    If( pStrictErrorHandling = 1 ); 
        ProcessQuit; 
    EndIf;
Else;
    sProcessAction = Expand( 'Process:%cThisProcName% successfully cloned the %pSrcCube% cube to %pTgtCube%.' );
    sProcessReturnCode = Expand( '%sProcessReturnCode% %sProcessAction%' );
    nProcessReturnCode = 1;
    If ( pLogOutput = 1 );
            LogOutput('INFO', Expand ( sProcessAction ) );
    EndIf;
EndIf;

### End Epilog ###
