IF( pLogOutput = 1 );
  LogOutput( 'INFO', 'Process completed successfully' );
ENDIF;

IF( JsonSize( sErrors ) <> 0 );
  LogOutput( 'ERROR', 'Process encountered the following errors: ' | sErrors );
  IF( pStrictErrorHandling = 1 );
    ProcessQuit();
  ENDIF;
ENDIF;
